import{_ as t,c as r,o,ag as a}from"./chunks/framework.CQuhCYrb.js";const g=JSON.parse('{"title":"Glossary","description":"","frontmatter":{},"headers":[],"relativePath":"reference/glossary.md","filePath":"reference/glossary.md"}'),i={name:"reference/glossary.md"};function n(s,e,l,h,d,c){return o(),r("div",null,[...e[0]||(e[0]=[a('<h1 id="glossary" tabindex="-1">Glossary <a class="header-anchor" href="#glossary" aria-label="Permalink to &quot;Glossary&quot;">​</a></h1><h2 id="a" tabindex="-1">A <a class="header-anchor" href="#a" aria-label="Permalink to &quot;A&quot;">​</a></h2><p><strong>ACL (Annual Copyright License)</strong> : A blanket license from CCC that covers routine internal copying and sharing of published content within an organization.</p><p><strong>Adaptation</strong> : A modification or derivative work based on an original copyrighted work.</p><p><strong>Author Agreement</strong> : The contract between an author and publisher that specifies copyright ownership and rights transfers.</p><h2 id="b" tabindex="-1">B <a class="header-anchor" href="#b" aria-label="Permalink to &quot;B&quot;">​</a></h2><p><strong>Blanket License</strong> : A license that covers multiple uses or items under a single agreement, typically for an annual fee.</p><h2 id="c" tabindex="-1">C <a class="header-anchor" href="#c" aria-label="Permalink to &quot;C&quot;">​</a></h2><p><strong>CC (Creative Commons)</strong> : A nonprofit organization that provides free, standardized licenses for creators to share their work with specific permissions.</p><p><strong>CC-BY</strong> : Creative Commons Attribution license - allows reuse with attribution.</p><p><strong>CC-BY-NC</strong> : Creative Commons Attribution-NonCommercial - allows non-commercial reuse with attribution.</p><p><strong>CCC (Copyright Clearance Center)</strong> : A licensing organization that facilitates copyright compliance through various licensing solutions.</p><p><strong>Copyright</strong> : Legal protection for original works of authorship, giving the creator exclusive rights to use and distribute.</p><p><strong>Copyright Notice</strong> : A statement indicating copyright ownership, typically &quot;© [Year] [Owner]&quot;.</p><p><strong>Corresponding Author</strong> : The author designated to handle communications about a publication, often the contact for permissions.</p><h2 id="d" tabindex="-1">D <a class="header-anchor" href="#d" aria-label="Permalink to &quot;D&quot;">​</a></h2><p><strong>Derivative Work</strong> : A new work based on one or more existing copyrighted works.</p><p><strong>DOI (Digital Object Identifier)</strong> : A unique alphanumeric string assigned to identify digital content and provide a permanent link.</p><h2 id="e" tabindex="-1">E <a class="header-anchor" href="#e" aria-label="Permalink to &quot;E&quot;">​</a></h2><p><strong>ELSA</strong> : Elsevier Author Platform - self-service platform for authors.</p><p><strong>EMSS</strong> : Electronic Manuscript Submission System - Elsevier&#39;s manuscript management system.</p><p><strong>Embargo Period</strong> : A period during which content cannot be freely shared, often applied to Open Access articles.</p><p><strong>Exclusive Rights</strong> : Rights granted to a single party, preventing others from the same use.</p><h2 id="f" tabindex="-1">F <a class="header-anchor" href="#f" aria-label="Permalink to &quot;F&quot;">​</a></h2><p><strong>Fair Use</strong> : A US legal doctrine allowing limited use of copyrighted material without permission for purposes like criticism, education, or research.</p><p><strong>FTE (Full-Time Equivalent)</strong> : A unit measuring workload equal to one full-time employee.</p><h2 id="g" tabindex="-1">G <a class="header-anchor" href="#g" aria-label="Permalink to &quot;G&quot;">​</a></h2><p><strong>Gold OA</strong> : Open Access publishing where the article is immediately freely available, typically with author-paid fees.</p><p><strong>Green OA</strong> : Open Access where authors self-archive their work in a repository, often after an embargo period.</p><p><strong>Grant of Rights</strong> : Permission given by a rights holder to use their copyrighted material.</p><h2 id="h" tabindex="-1">H <a class="header-anchor" href="#h" aria-label="Permalink to &quot;H&quot;">​</a></h2><p><strong>HCM (Health Content Management)</strong> : Elsevier&#39;s division managing health-related content.</p><p><strong>HS (Health Sciences)</strong> : Division handling medical and healthcare-related permissions.</p><h2 id="i" tabindex="-1">I <a class="header-anchor" href="#i" aria-label="Permalink to &quot;I&quot;">​</a></h2><p><strong>In-Copyright</strong> : A work that is still protected by copyright law.</p><p><strong>Intellectual Property (IP)</strong> : Legal rights over creations of the mind, including copyrights, patents, and trademarks.</p><h2 id="l" tabindex="-1">L <a class="header-anchor" href="#l" aria-label="Permalink to &quot;L&quot;">​</a></h2><p><strong>License</strong> : A legal agreement granting permission to use copyrighted material under specified conditions.</p><p><strong>License Fee</strong> : Payment required to obtain permission to use copyrighted material.</p><h2 id="m" tabindex="-1">M <a class="header-anchor" href="#m" aria-label="Permalink to &quot;M&quot;">​</a></h2><p><strong>Model Release</strong> : Written permission from a person appearing in a photo or video to use their likeness.</p><h2 id="n" tabindex="-1">N <a class="header-anchor" href="#n" aria-label="Permalink to &quot;N&quot;">​</a></h2><p><strong>Non-Exclusive Rights</strong> : Rights that can be granted to multiple parties simultaneously.</p><h2 id="o" tabindex="-1">O <a class="header-anchor" href="#o" aria-label="Permalink to &quot;O&quot;">​</a></h2><p><strong>OA (Open Access)</strong> : A publishing model where content is freely available to readers without subscription.</p><p><strong>ORCID</strong> : Open Researcher and Contributor ID - a unique identifier for researchers.</p><h2 id="p" tabindex="-1">P <a class="header-anchor" href="#p" aria-label="Permalink to &quot;P&quot;">​</a></h2><p><strong>Permission</strong> : Authorization from a rights holder to use copyrighted material.</p><p><strong>PMID (PubMed ID)</strong> : A unique identifier for articles indexed in PubMed.</p><p><strong>PMC (PubMed Central)</strong> : NIH&#39;s free archive of biomedical and life sciences literature.</p><p><strong>Public Domain</strong> : Works not protected by copyright, freely available for use.</p><h2 id="r" tabindex="-1">R <a class="header-anchor" href="#r" aria-label="Permalink to &quot;R&quot;">​</a></h2><p><strong>Republication</strong> : Using previously published content in a new publication.</p><p><strong>Rights Holder</strong> : The person or entity that owns the copyright to a work.</p><p><strong>RightsLink</strong> : CCC&#39;s online licensing platform for purchasing permissions.</p><p><strong>RPC (Rights &amp; Permissions Collector)</strong> : Elsevier&#39;s system of record for managing permissions.</p><p><strong>Rule of 10</strong> : Policy requiring publisher contact when requesting 10+ items from the same source.</p><h2 id="s" tabindex="-1">S <a class="header-anchor" href="#s" aria-label="Permalink to &quot;S&quot;">​</a></h2><p><strong>S&amp;T (Science &amp; Technology)</strong> : Division handling scientific and technical permissions.</p><p><strong>SHERPA/RoMEO</strong> : Database of publisher copyright and self-archiving policies.</p><p><strong>STM</strong> : International Association of Scientific, Technical and Medical Publishers.</p><p><strong>STM Guidelines</strong> : Framework for reasonable reuse of content between STM publishers.</p><p><strong>System of Record</strong> : The authoritative data source for a given data element (RPC for permissions).</p><h2 id="t" tabindex="-1">T <a class="header-anchor" href="#t" aria-label="Permalink to &quot;T&quot;">​</a></h2><p><strong>Territory</strong> : Geographic scope of a license (e.g., worldwide, North America only).</p><p><strong>Tier</strong> : Classification level for permission requests based on complexity.</p><p><strong>Transfer of Copyright</strong> : When an author assigns their copyright to a publisher.</p><h2 id="u" tabindex="-1">U <a class="header-anchor" href="#u" aria-label="Permalink to &quot;U&quot;">​</a></h2><p><strong>Usage Rights</strong> : Specific permissions for how content may be used (print, electronic, translations, etc.).</p><h2 id="w" tabindex="-1">W <a class="header-anchor" href="#w" aria-label="Permalink to &quot;W&quot;">​</a></h2><p><strong>Work for Hire</strong> : Work created by an employee within scope of employment, with copyright owned by employer.</p><p><strong>World Rights</strong> : License granting permission for use anywhere in the world.</p><hr><h2 id="abbreviations-quick-reference" tabindex="-1">Abbreviations Quick Reference <a class="header-anchor" href="#abbreviations-quick-reference" aria-label="Permalink to &quot;Abbreviations Quick Reference&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Abbreviation</th><th>Full Term</th></tr></thead><tbody><tr><td>CCC</td><td>Copyright Clearance Center</td></tr><tr><td>CC</td><td>Creative Commons</td></tr><tr><td>DOI</td><td>Digital Object Identifier</td></tr><tr><td>ELSA</td><td>Elsevier Author Platform</td></tr><tr><td>EMSS</td><td>Electronic Manuscript Submission System</td></tr><tr><td>FTE</td><td>Full-Time Equivalent</td></tr><tr><td>HCM</td><td>Health Content Management</td></tr><tr><td>HS</td><td>Health Sciences</td></tr><tr><td>IP</td><td>Intellectual Property</td></tr><tr><td>OA</td><td>Open Access</td></tr><tr><td>ORCID</td><td>Open Researcher and Contributor ID</td></tr><tr><td>PMID</td><td>PubMed ID</td></tr><tr><td>PMC</td><td>PubMed Central</td></tr><tr><td>RPC</td><td>Rights &amp; Permissions Collector</td></tr><tr><td>S&amp;T</td><td>Science &amp; Technology</td></tr><tr><td>STM</td><td>Scientific, Technical and Medical</td></tr></tbody></table>',75)])])}const u=t(i,[["render",n]]);export{g as __pageData,u as default};
