import{_ as t,c as i,o as a,ag as r}from"./chunks/framework.CQuhCYrb.js";const s="/diagrams/17_STM_Guidelines_Decision_Flow.svg",m=JSON.parse('{"title":"STM Permissions Guidelines","description":"","frontmatter":{},"headers":[],"relativePath":"reference/stm-guidelines.md","filePath":"reference/stm-guidelines.md"}'),o={name:"reference/stm-guidelines.md"};function l(n,e,d,h,c,u){return a(),i("div",null,[...e[0]||(e[0]=[r('<h1 id="stm-permissions-guidelines" tabindex="-1">STM Permissions Guidelines <a class="header-anchor" href="#stm-permissions-guidelines" aria-label="Permalink to &quot;STM Permissions Guidelines&quot;">​</a></h1><h2 id="quick-decision-flow" tabindex="-1">Quick Decision Flow <a class="header-anchor" href="#quick-decision-flow" aria-label="Permalink to &quot;Quick Decision Flow&quot;">​</a></h2><p>Use this flowchart to determine if STM Guidelines apply to your request:</p><p><img src="'+s+`" alt="STM Guidelines Decision Flow"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The <strong>STM (International Association of Scientific, Technical and Medical Publishers) Permissions Guidelines</strong> provide a framework for reasonable reuse of published content within the academic/scientific community.</p><h2 id="what-are-stm-guidelines" tabindex="-1">What are STM Guidelines? <a class="header-anchor" href="#what-are-stm-guidelines" aria-label="Permalink to &quot;What are STM Guidelines?&quot;">​</a></h2><p>The STM Guidelines establish best practices for:</p><ul><li>Granting permissions between STM publishers</li><li>Defining reasonable use thresholds</li><li>Standardizing terms and conditions</li><li>Facilitating scholarly communication</li></ul><h2 id="key-principles" tabindex="-1">Key Principles <a class="header-anchor" href="#key-principles" aria-label="Permalink to &quot;Key Principles&quot;">​</a></h2><h3 id="reciprocity" tabindex="-1">Reciprocity <a class="header-anchor" href="#reciprocity" aria-label="Permalink to &quot;Reciprocity&quot;">​</a></h3><p>STM member publishers agree to:</p><ul><li>Grant permissions to each other&#39;s authors</li><li>Use standardized terms</li><li>Waive or minimize fees for qualifying uses</li><li>Process requests promptly</li></ul><h3 id="reasonable-use" tabindex="-1">Reasonable Use <a class="header-anchor" href="#reasonable-use" aria-label="Permalink to &quot;Reasonable Use&quot;">​</a></h3><p>Defined thresholds for use without fee:</p><ul><li>Up to 3 figures/tables from a journal article</li><li>Up to 3 figures/tables from a book chapter</li><li>Text excerpts under defined word limits</li></ul><h2 id="qualifying-conditions" tabindex="-1">Qualifying Conditions <a class="header-anchor" href="#qualifying-conditions" aria-label="Permalink to &quot;Qualifying Conditions&quot;">​</a></h2><h3 id="for-stm-guidelines-to-apply" tabindex="-1">For STM Guidelines to Apply <a class="header-anchor" href="#for-stm-guidelines-to-apply" aria-label="Permalink to &quot;For STM Guidelines to Apply&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Requirement</th><th>Details</th></tr></thead><tbody><tr><td><strong>Both parties STM members</strong></td><td>Check membership list</td></tr><tr><td><strong>Scholarly purpose</strong></td><td>Academic/educational use</td></tr><tr><td><strong>Proper attribution</strong></td><td>Full citation required</td></tr><tr><td><strong>Limited quantity</strong></td><td>Within thresholds</td></tr><tr><td><strong>Non-competing</strong></td><td>Not a competing product</td></tr></tbody></table><h3 id="exclusions" tabindex="-1">Exclusions <a class="header-anchor" href="#exclusions" aria-label="Permalink to &quot;Exclusions&quot;">​</a></h3><p>STM Guidelines typically do NOT cover:</p><ul><li>Commercial products</li><li>Marketing materials</li><li>Complete articles</li><li>Systematic reproduction</li><li>Non-STM publishers</li></ul><h2 id="quantitative-thresholds" tabindex="-1">Quantitative Thresholds <a class="header-anchor" href="#quantitative-thresholds" aria-label="Permalink to &quot;Quantitative Thresholds&quot;">​</a></h2><h3 id="figures-and-tables" tabindex="-1">Figures and Tables <a class="header-anchor" href="#figures-and-tables" aria-label="Permalink to &quot;Figures and Tables&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Source Type</th><th>Threshold</th></tr></thead><tbody><tr><td><strong>Journal Article</strong></td><td>Up to 3 figures/tables</td></tr><tr><td><strong>Book Chapter</strong></td><td>Up to 3 figures/tables</td></tr><tr><td><strong>Entire Book</strong></td><td>Subject to publisher policy</td></tr></tbody></table><h3 id="text-excerpts" tabindex="-1">Text Excerpts <a class="header-anchor" href="#text-excerpts" aria-label="Permalink to &quot;Text Excerpts&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Source Type</th><th>Threshold</th></tr></thead><tbody><tr><td><strong>Journal Article</strong></td><td>Up to 400 words single extract</td></tr><tr><td><strong>Journal Article</strong></td><td>Up to 800 words total</td></tr><tr><td><strong>Book</strong></td><td>Varies by publisher</td></tr></tbody></table><h3 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h3><ul><li>Thresholds are cumulative across requests</li><li>Check if content was previously used</li><li>Different publishers may have variations</li></ul><h2 id="applying-stm-guidelines" tabindex="-1">Applying STM Guidelines <a class="header-anchor" href="#applying-stm-guidelines" aria-label="Permalink to &quot;Applying STM Guidelines&quot;">​</a></h2><h3 id="step-1-verify-eligibility" tabindex="-1">Step 1: Verify Eligibility <a class="header-anchor" href="#step-1-verify-eligibility" aria-label="Permalink to &quot;Step 1: Verify Eligibility&quot;">​</a></h3><ol><li>Is the source publisher an STM member?</li><li>Is Elsevier an STM member? (Yes)</li><li>Is the use scholarly/educational?</li><li>Is the quantity within thresholds?</li></ol><h3 id="step-2-check-specific-terms" tabindex="-1">Step 2: Check Specific Terms <a class="header-anchor" href="#step-2-check-specific-terms" aria-label="Permalink to &quot;Step 2: Check Specific Terms&quot;">​</a></h3><p>Even with STM, publishers may:</p><ul><li>Have specific requirements</li><li>Require written confirmation</li><li>Need credit line in specific format</li><li>Have additional restrictions</li></ul><h3 id="step-3-document-compliance" tabindex="-1">Step 3: Document Compliance <a class="header-anchor" href="#step-3-document-compliance" aria-label="Permalink to &quot;Step 3: Document Compliance&quot;">​</a></h3><p>Record in RPC:</p><ul><li>STM Guidelines cited</li><li>Threshold verification</li><li>Any specific terms from publisher</li><li>Credit line used</li></ul><h2 id="stm-member-lookup" tabindex="-1">STM Member Lookup <a class="header-anchor" href="#stm-member-lookup" aria-label="Permalink to &quot;STM Member Lookup&quot;">​</a></h2><h3 id="how-to-verify" tabindex="-1">How to Verify <a class="header-anchor" href="#how-to-verify" aria-label="Permalink to &quot;How to Verify&quot;">​</a></h3><ol><li>Visit stm-assoc.org</li><li>Check member directory</li><li>Or search publisher permissions page</li></ol><h3 id="major-stm-members" tabindex="-1">Major STM Members <a class="header-anchor" href="#major-stm-members" aria-label="Permalink to &quot;Major STM Members&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Publisher</th><th>STM Member</th></tr></thead><tbody><tr><td>Springer Nature</td><td>Yes</td></tr><tr><td>Wiley</td><td>Yes</td></tr><tr><td>Elsevier</td><td>Yes</td></tr><tr><td>Taylor &amp; Francis</td><td>Yes</td></tr><tr><td>SAGE</td><td>Yes</td></tr><tr><td>OUP</td><td>Yes</td></tr><tr><td>ACS</td><td>Yes</td></tr></tbody></table><p><em>Always verify current status</em></p><h2 id="credit-line-requirements" tabindex="-1">Credit Line Requirements <a class="header-anchor" href="#credit-line-requirements" aria-label="Permalink to &quot;Credit Line Requirements&quot;">​</a></h2><h3 id="standard-format" tabindex="-1">Standard Format <a class="header-anchor" href="#standard-format" aria-label="Permalink to &quot;Standard Format&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Reprinted from [Article Title], [Authors], [Journal],</span></span>
<span class="line"><span>Vol. [X], Issue [Y], Pages [Z], Copyright [Year],</span></span>
<span class="line"><span>with permission from [Publisher].</span></span></code></pre></div><h3 id="example" tabindex="-1">Example <a class="header-anchor" href="#example" aria-label="Permalink to &quot;Example&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Reprinted from &quot;Advances in Gene Therapy,&quot; Smith et al.,</span></span>
<span class="line"><span>Nature Medicine, Vol. 25, Issue 3, Pages 123-130,</span></span>
<span class="line"><span>Copyright 2024, with permission from Springer Nature.</span></span></code></pre></div><h2 id="common-scenarios" tabindex="-1">Common Scenarios <a class="header-anchor" href="#common-scenarios" aria-label="Permalink to &quot;Common Scenarios&quot;">​</a></h2><h3 id="scenario-1-within-thresholds" tabindex="-1">Scenario 1: Within Thresholds <a class="header-anchor" href="#scenario-1-within-thresholds" aria-label="Permalink to &quot;Scenario 1: Within Thresholds&quot;">​</a></h3><p><strong>Situation</strong>: Using 2 figures from a Nature article</p><p><strong>Action</strong>:</p><ul><li>Verify Springer Nature STM membership</li><li>Confirm scholarly use</li><li>Apply STM terms</li><li>May not require formal request</li><li>Document in RPC</li></ul><h3 id="scenario-2-exceeds-thresholds" tabindex="-1">Scenario 2: Exceeds Thresholds <a class="header-anchor" href="#scenario-2-exceeds-thresholds" aria-label="Permalink to &quot;Scenario 2: Exceeds Thresholds&quot;">​</a></h3><p><strong>Situation</strong>: Need 5 figures from same article</p><p><strong>Action</strong>:</p><ul><li>STM doesn&#39;t fully apply</li><li>Contact publisher for permission</li><li>May involve fee</li><li>Document specific approval</li></ul><h3 id="scenario-3-non-member-publisher" tabindex="-1">Scenario 3: Non-Member Publisher <a class="header-anchor" href="#scenario-3-non-member-publisher" aria-label="Permalink to &quot;Scenario 3: Non-Member Publisher&quot;">​</a></h3><p><strong>Situation</strong>: Content from non-STM publisher</p><p><strong>Action</strong>:</p><ul><li>STM Guidelines don&#39;t apply</li><li>Standard permission request required</li><li>Follow normal tier workflow</li></ul><h2 id="limitations-and-cautions" tabindex="-1">Limitations and Cautions <a class="header-anchor" href="#limitations-and-cautions" aria-label="Permalink to &quot;Limitations and Cautions&quot;">​</a></h2><h3 id="what-stm-doesn-t-cover" tabindex="-1">What STM Doesn&#39;t Cover <a class="header-anchor" href="#what-stm-doesn-t-cover" aria-label="Permalink to &quot;What STM Doesn&#39;t Cover&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Not Covered</th><th>Why</th></tr></thead><tbody><tr><td><strong>Complete works</strong></td><td>Beyond reasonable use</td></tr><tr><td><strong>Systematic copying</strong></td><td>Commercial concern</td></tr><tr><td><strong>Non-academic use</strong></td><td>Outside guidelines scope</td></tr><tr><td><strong>Original artwork</strong></td><td>Special considerations</td></tr><tr><td><strong>Patient photos</strong></td><td>Privacy concerns</td></tr></tbody></table><h3 id="when-to-still-request" tabindex="-1">When to Still Request <a class="header-anchor" href="#when-to-still-request" aria-label="Permalink to &quot;When to Still Request&quot;">​</a></h3><p>Even within thresholds, request permission when:</p><ul><li>Publisher requires written confirmation</li><li>High-profile content</li><li>Potential competing use</li><li>Any uncertainty</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="documentation" tabindex="-1">Documentation <a class="header-anchor" href="#documentation" aria-label="Permalink to &quot;Documentation&quot;">​</a></h3><ul><li>Note STM applicability in RPC</li><li>Record threshold calculations</li><li>Save any publisher confirmations</li><li>Document credit line used</li></ul><h3 id="quality-assurance" tabindex="-1">Quality Assurance <a class="header-anchor" href="#quality-assurance" aria-label="Permalink to &quot;Quality Assurance&quot;">​</a></h3><ul><li>Double-check member status</li><li>Verify threshold calculations</li><li>Confirm use is truly scholarly</li><li>Review specific publisher requirements</li></ul><h2 id="resources" tabindex="-1">Resources <a class="header-anchor" href="#resources" aria-label="Permalink to &quot;Resources&quot;">​</a></h2><h3 id="official-documentation" tabindex="-1">Official Documentation <a class="header-anchor" href="#official-documentation" aria-label="Permalink to &quot;Official Documentation&quot;">​</a></h3><ul><li>STM Association: stm-assoc.org</li><li>Guidelines document: Available on STM site</li><li>Member directory: Updated regularly</li></ul><h3 id="internal-resources" tabindex="-1">Internal Resources <a class="header-anchor" href="#internal-resources" aria-label="Permalink to &quot;Internal Resources&quot;">​</a></h3><ul><li>Team Lead for interpretation</li><li>Legal for unusual cases</li><li>Publisher relations for disputes</li></ul>`,78)])])}const b=t(o,[["render",l]]);export{m as __pageData,b as default};
