import{_ as t,c as a,o as n,ag as i}from"./chunks/framework.CQuhCYrb.js";const r="/diagrams/06_Followup_Escalation_Cadence.svg",m=JSON.parse('{"title":"Follow-up & Escalation","description":"","frontmatter":{},"headers":[],"relativePath":"workflows/followup-escalation.md","filePath":"workflows/followup-escalation.md"}'),o={name:"workflows/followup-escalation.md"};function s(l,e,d,c,h,p){return n(),a("div",null,[...e[0]||(e[0]=[i('<h1 id="follow-up-escalation" tabindex="-1">Follow-up &amp; Escalation <a class="header-anchor" href="#follow-up-escalation" aria-label="Permalink to &quot;Follow-up &amp; Escalation&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Systematic follow-up is critical to permission success. This guide covers timing, methods, and escalation procedures.</p><p><img src="'+r+`" alt="Follow-up &amp; Escalation Cadence"></p><h2 id="standard-follow-up-cadence" tabindex="-1">Standard Follow-up Cadence <a class="header-anchor" href="#standard-follow-up-cadence" aria-label="Permalink to &quot;Standard Follow-up Cadence&quot;">​</a></h2><p>The permission workflow uses a structured reminder schedule tracked in RPC:</p><h3 id="reminder-schedule-from-rpc-chapter-tracker" tabindex="-1">Reminder Schedule (from RPC Chapter Tracker) <a class="header-anchor" href="#reminder-schedule-from-rpc-chapter-tracker" aria-label="Permalink to &quot;Reminder Schedule (from RPC Chapter Tracker)&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Stage</th><th>Description</th></tr></thead><tbody><tr><td><strong>Intro Email</strong></td><td>Initial outreach to author/rightsholder</td></tr><tr><td><strong>First Reminder</strong></td><td>First follow-up if no response</td></tr><tr><td><strong>Second Reminder</strong></td><td>Second follow-up</td></tr><tr><td><strong>First Chaser</strong></td><td>More urgent follow-up</td></tr><tr><td><strong>Second Chaser</strong></td><td>Final follow-up</td></tr><tr><td><strong>Third Chaser</strong></td><td>Last attempt before decision</td></tr></tbody></table><h3 id="rpc-tracking-fields" tabindex="-1">RPC Tracking Fields <a class="header-anchor" href="#rpc-tracking-fields" aria-label="Permalink to &quot;RPC Tracking Fields&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Field</th><th>Purpose</th></tr></thead><tbody><tr><td>Intro Email Due Date</td><td>Scheduled initial outreach</td></tr><tr><td>Intro Email Sent Date</td><td>Confirmation of send</td></tr><tr><td>MS Due Date</td><td>Manuscript deadline</td></tr><tr><td>First Reminder Due/Sent Date</td><td>First follow-up tracking</td></tr><tr><td>Second Reminder Due/Sent Date</td><td>Second follow-up tracking</td></tr><tr><td>First Chaser Due/Sent Date</td><td>First chaser tracking</td></tr><tr><td>Second Chaser Due/Sent Date</td><td>Second chaser tracking</td></tr></tbody></table><h2 id="follow-up-email-guidelines" tabindex="-1">Follow-up Email Guidelines <a class="header-anchor" href="#follow-up-email-guidelines" aria-label="Permalink to &quot;Follow-up Email Guidelines&quot;">​</a></h2><h3 id="first-reminder" tabindex="-1">First Reminder <a class="header-anchor" href="#first-reminder" aria-label="Permalink to &quot;First Reminder&quot;">​</a></h3><p><strong>Tone</strong>: Friendly reminder <strong>Content</strong>:</p><ul><li>Reference original request date</li><li>Restate the specific content needed</li><li>Offer to clarify any questions</li><li>Provide deadline if applicable</li></ul><h3 id="second-reminder" tabindex="-1">Second Reminder <a class="header-anchor" href="#second-reminder" aria-label="Permalink to &quot;Second Reminder&quot;">​</a></h3><p><strong>Tone</strong>: More direct <strong>Content</strong>:</p><ul><li>Acknowledge their busy schedule</li><li>Emphasize importance/timeline</li><li>Offer alternative contact methods</li><li>Suggest simple response options</li></ul><h3 id="chaser-emails" tabindex="-1">Chaser Emails <a class="header-anchor" href="#chaser-emails" aria-label="Permalink to &quot;Chaser Emails&quot;">​</a></h3><p><strong>Tone</strong>: Urgent but professional <strong>Content</strong>:</p><ul><li>Clear deadline statement</li><li>Consequences of non-response</li><li>Final opportunity to respond</li><li>Alternative options if no response</li></ul><h2 id="escalation-procedures" tabindex="-1">Escalation Procedures <a class="header-anchor" href="#escalation-procedures" aria-label="Permalink to &quot;Escalation Procedures&quot;">​</a></h2><h3 id="when-to-escalate" tabindex="-1">When to Escalate <a class="header-anchor" href="#when-to-escalate" aria-label="Permalink to &quot;When to Escalate&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Trigger</th><th>Escalation Path</th></tr></thead><tbody><tr><td>No response after full cadence</td><td>Team Lead review</td></tr><tr><td>Unreasonable fee quoted</td><td>Manager approval</td></tr><tr><td>Legal/terms concerns</td><td>Legal department</td></tr><tr><td>High-value content needed</td><td>Manager decision</td></tr><tr><td>Author dispute</td><td>Senior specialist</td></tr><tr><td>Timeline at risk</td><td>Editorial + Manager</td></tr></tbody></table><h3 id="escalation-levels" tabindex="-1">Escalation Levels <a class="header-anchor" href="#escalation-levels" aria-label="Permalink to &quot;Escalation Levels&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Level 1: Self-Resolution</span></span>
<span class="line"><span>├── Follow standard cadence</span></span>
<span class="line"><span>├── Try alternative contacts</span></span>
<span class="line"><span>└── Document all attempts</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Level 2: Team Lead</span></span>
<span class="line"><span>├── Review case history</span></span>
<span class="line"><span>├── Suggest alternative approaches</span></span>
<span class="line"><span>└── May contact rights holder directly</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Level 3: Manager</span></span>
<span class="line"><span>├── Fee approval authority</span></span>
<span class="line"><span>├── Publisher relationship leverage</span></span>
<span class="line"><span>└── Editorial timeline negotiations</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Level 4: Legal/Executive</span></span>
<span class="line"><span>├── Complex rights disputes</span></span>
<span class="line"><span>├── Litigation risk</span></span>
<span class="line"><span>└── Policy decisions</span></span></code></pre></div><h2 id="alternative-contact-strategies" tabindex="-1">Alternative Contact Strategies <a class="header-anchor" href="#alternative-contact-strategies" aria-label="Permalink to &quot;Alternative Contact Strategies&quot;">​</a></h2><h3 id="when-primary-contact-fails" tabindex="-1">When Primary Contact Fails <a class="header-anchor" href="#when-primary-contact-fails" aria-label="Permalink to &quot;When Primary Contact Fails&quot;">​</a></h3><ol><li><strong>Co-Authors</strong> - Contact other listed authors</li><li><strong>Institution</strong> - Try departmental/university contact</li><li><strong>Publisher</strong> - Even for author-owned content</li><li><strong>Professional Networks</strong> - LinkedIn, ResearchGate</li><li><strong>Phone</strong> - If available (use sparingly)</li></ol><h3 id="research-strategies" tabindex="-1">Research Strategies <a class="header-anchor" href="#research-strategies" aria-label="Permalink to &quot;Research Strategies&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Source</th><th>How to Use</th></tr></thead><tbody><tr><td><strong>Google Search</strong></td><td>&quot;[Author name] + contact email&quot;</td></tr><tr><td><strong>Institution Site</strong></td><td>Faculty directory</td></tr><tr><td><strong>Recent Papers</strong></td><td>May have updated contact</td></tr><tr><td><strong>ORCID</strong></td><td>Often maintained</td></tr><tr><td><strong>Conference Sites</strong></td><td>Speaker contact info</td></tr></tbody></table><h2 id="handling-non-response" tabindex="-1">Handling Non-Response <a class="header-anchor" href="#handling-non-response" aria-label="Permalink to &quot;Handling Non-Response&quot;">​</a></h2><h3 id="after-full-cadence-decision-matrix" tabindex="-1">After Full Cadence - Decision Matrix <a class="header-anchor" href="#after-full-cadence-decision-matrix" aria-label="Permalink to &quot;After Full Cadence - Decision Matrix&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Situation</th><th>Recommended Action</th></tr></thead><tbody><tr><td>Content essential, no alternatives</td><td>Escalate to Manager</td></tr><tr><td>Alternative available</td><td>Document, use alternative</td></tr><tr><td>Low priority item</td><td>Close as unresponsive</td></tr><tr><td>Publisher may own rights</td><td>Contact publisher</td></tr><tr><td>Legal risk if used</td><td>Do not use, document</td></tr></tbody></table><h3 id="documentation-for-non-response" tabindex="-1">Documentation for Non-Response <a class="header-anchor" href="#documentation-for-non-response" aria-label="Permalink to &quot;Documentation for Non-Response&quot;">​</a></h3><p>Record in RPC:</p><ul><li>All contact attempts with dates</li><li>Channels used</li><li>Contact information tried</li><li>Final decision and rationale</li><li>Alternative action taken</li></ul><h2 id="response-handling" tabindex="-1">Response Handling <a class="header-anchor" href="#response-handling" aria-label="Permalink to &quot;Response Handling&quot;">​</a></h2><h3 id="permission-granted" tabindex="-1">Permission Granted <a class="header-anchor" href="#permission-granted" aria-label="Permalink to &quot;Permission Granted&quot;">​</a></h3><ol><li>Confirm terms match request</li><li>Document any conditions</li><li>Process any fees</li><li>Update RPC status</li><li>Provide credit line to editorial</li><li>Archive correspondence</li></ol><h3 id="permission-denied" tabindex="-1">Permission Denied <a class="header-anchor" href="#permission-denied" aria-label="Permalink to &quot;Permission Denied&quot;">​</a></h3><ol><li>Document reason for denial</li><li>Assess if negotiation possible</li><li>Notify editorial immediately</li><li>Research alternatives</li><li>Update RPC with outcome</li></ol><h3 id="conditional-permission" tabindex="-1">Conditional Permission <a class="header-anchor" href="#conditional-permission" aria-label="Permalink to &quot;Conditional Permission&quot;">​</a></h3><ol><li>Review conditions carefully</li><li>Assess if acceptable</li><li>Escalate unusual conditions</li><li>Get editorial approval if needed</li><li>Document exact terms</li><li>Track any follow-up requirements</li></ol><h2 id="timeline-management" tabindex="-1">Timeline Management <a class="header-anchor" href="#timeline-management" aria-label="Permalink to &quot;Timeline Management&quot;">​</a></h2><h3 id="proactive-communication" tabindex="-1">Proactive Communication <a class="header-anchor" href="#proactive-communication" aria-label="Permalink to &quot;Proactive Communication&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Milestone</th><th>Action</th></tr></thead><tbody><tr><td>Project Start</td><td>Assess permission volume</td></tr><tr><td>50% Complete</td><td>Review at-risk items</td></tr><tr><td>75% Complete</td><td>Escalate blockers</td></tr><tr><td>Final Deadline - 2 weeks</td><td>Status report to editorial</td></tr><tr><td>Final Deadline - 1 week</td><td>Emergency escalation</td></tr></tbody></table><h3 id="at-risk-indicators" tabindex="-1">At-Risk Indicators <a class="header-anchor" href="#at-risk-indicators" aria-label="Permalink to &quot;At-Risk Indicators&quot;">​</a></h3><ul><li>Multiple items unresponsive from same source</li><li>High-fee items awaiting approval</li><li>Complex negotiations ongoing</li><li>Legal review pending</li><li>Editorial changes affecting scope</li></ul><h2 id="key-timeliness-target" tabindex="-1">Key Timeliness Target <a class="header-anchor" href="#key-timeliness-target" aria-label="Permalink to &quot;Key Timeliness Target&quot;">​</a></h2><p>The primary KPI for permissions workflow is <strong>completion 30 days prior to Files To Printer (FTP) date</strong>.</p>`,50)])])}const g=t(o,[["render",s]]);export{m as __pageData,g as default};
