import{_ as e,c as i,o as r,ag as o}from"./chunks/framework.CQuhCYrb.js";const a="/diagrams/19_HS_vs_ST_Comparison.svg",p=JSON.parse('{"title":"Health Sciences (HS) Workflows","description":"","frontmatter":{},"headers":[],"relativePath":"workflows/health-sciences.md","filePath":"workflows/health-sciences.md"}'),s={name:"workflows/health-sciences.md"};function n(l,t,d,c,h,u){return r(),i("div",null,[...t[0]||(t[0]=[o('<h1 id="health-sciences-hs-workflows" tabindex="-1">Health Sciences (HS) Workflows <a class="header-anchor" href="#health-sciences-hs-workflows" aria-label="Permalink to &quot;Health Sciences (HS) Workflows&quot;">​</a></h1><h2 id="hs-vs-s-t-at-a-glance" tabindex="-1">HS vs S&amp;T at a Glance <a class="header-anchor" href="#hs-vs-s-t-at-a-glance" aria-label="Permalink to &quot;HS vs S&amp;T at a Glance&quot;">​</a></h2><p><img src="'+a+'" alt="HS vs S&amp;T Comparison"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Health Sciences (HS) permissions handle medical textbooks, clinical references, and healthcare education materials. The HS division uses a specific tier structure tailored to health content workflows.</p><h3 id="volume" tabindex="-1">Volume <a class="header-anchor" href="#volume" aria-label="Permalink to &quot;Volume&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Metric</th><th>Value</th></tr></thead><tbody><tr><td><strong>HS Projects/Year</strong></td><td>770</td></tr></tbody></table><h2 id="hs-specific-characteristics" tabindex="-1">HS-Specific Characteristics <a class="header-anchor" href="#hs-specific-characteristics" aria-label="Permalink to &quot;HS-Specific Characteristics&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Aspect</th><th>HS Characteristics</th></tr></thead><tbody><tr><td><strong>Content Types</strong></td><td>Medical images, clinical photos, anatomical diagrams</td></tr><tr><td><strong>Rights Holders</strong></td><td>Medical journals, healthcare publishers, institutions</td></tr><tr><td><strong>Sensitivity</strong></td><td>Patient privacy, clinical accuracy critical</td></tr></tbody></table><h2 id="hs-tier-definitions" tabindex="-1">HS Tier Definitions <a class="header-anchor" href="#hs-tier-definitions" aria-label="Permalink to &quot;HS Tier Definitions&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Tier</th><th>Description</th></tr></thead><tbody><tr><td><strong>Tier 0</strong></td><td>CDS/Author obtains all permissions. CS validates and uploads logs/files to RPC</td></tr><tr><td><strong>Tier 4</strong></td><td>CDS provides permission log. CS obtains permissions. CS uploads logs/files to RPC</td></tr><tr><td><strong>Rule of 10</strong></td><td>No permission seeking - CS only does Rule of 10 assessment</td></tr></tbody></table><h3 id="role-key" tabindex="-1">Role Key <a class="header-anchor" href="#role-key" aria-label="Permalink to &quot;Role Key&quot;">​</a></h3><ul><li><strong>CDS</strong> = Content Development Specialist</li><li><strong>CS</strong> = Copyrights Specialist</li></ul><h2 id="tier-0-hs-workflow" tabindex="-1">Tier 0 - HS Workflow <a class="header-anchor" href="#tier-0-hs-workflow" aria-label="Permalink to &quot;Tier 0 - HS Workflow&quot;">​</a></h2><h3 id="eligibility-check" tabindex="-1">Eligibility Check <a class="header-anchor" href="#eligibility-check" aria-label="Permalink to &quot;Eligibility Check&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Check</th><th>Action</th></tr></thead><tbody><tr><td><strong>Copyright Status</strong></td><td>Verify public domain or CC license</td></tr><tr><td><strong>Elsevier Ownership</strong></td><td>Check internal rights database</td></tr><tr><td><strong>STM Compliance</strong></td><td>Verify meets STM Guidelines thresholds</td></tr><tr><td><strong>Source Verification</strong></td><td>Confirm original source</td></tr></tbody></table><h3 id="documentation-requirements" tabindex="-1">Documentation Requirements <a class="header-anchor" href="#documentation-requirements" aria-label="Permalink to &quot;Documentation Requirements&quot;">​</a></h3><ul><li>Screenshot or link to license/PD evidence</li><li>Source publication details</li><li>Specific clause or guideline reference</li><li>RPC closure notes</li></ul><h3 id="common-tier-0-sources" tabindex="-1">Common Tier 0 Sources <a class="header-anchor" href="#common-tier-0-sources" aria-label="Permalink to &quot;Common Tier 0 Sources&quot;">​</a></h3><ol><li><strong>Government Health Agencies</strong> - CDC, NIH, WHO public materials</li><li><strong>Open Access Journals</strong> - PMC, PLOS, BMC with appropriate licenses</li><li><strong>Historical Medical Texts</strong> - Pre-1928 publications (US)</li><li><strong>Elsevier Archive</strong> - Previously cleared or owned content</li></ol><hr><h2 id="tier-4-hs-workflow" tabindex="-1">Tier 4 - HS Workflow <a class="header-anchor" href="#tier-4-hs-workflow" aria-label="Permalink to &quot;Tier 4 - HS Workflow&quot;">​</a></h2><h3 id="initial-processing" tabindex="-1">Initial Processing <a class="header-anchor" href="#initial-processing" aria-label="Permalink to &quot;Initial Processing&quot;">​</a></h3><ol><li><strong>Item Receipt</strong> - New item appears in RPC queue</li><li><strong>Source Review</strong> - Examine original publication</li><li><strong>Author Identification</strong> - Find corresponding/rights-holding author</li><li><strong>Contact Search</strong> - Locate current email address</li></ol><h3 id="author-contact-strategies" tabindex="-1">Author Contact Strategies <a class="header-anchor" href="#author-contact-strategies" aria-label="Permalink to &quot;Author Contact Strategies&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Source</th><th>Method</th></tr></thead><tbody><tr><td><strong>Publication Contact Info</strong></td><td>Use email from paper</td></tr><tr><td><strong>Institutional Directory</strong></td><td>Search university/hospital sites</td></tr><tr><td><strong>ORCID/Google Scholar</strong></td><td>Academic profile lookup</td></tr><tr><td><strong>PubMed/CrossRef</strong></td><td>Citation database search</td></tr><tr><td><strong>LinkedIn</strong></td><td>Professional network (last resort)</td></tr></tbody></table><h3 id="request-content" tabindex="-1">Request Content <a class="header-anchor" href="#request-content" aria-label="Permalink to &quot;Request Content&quot;">​</a></h3><p>Standard Tier 4 request includes:</p><ul><li>Clear identification of content needed</li><li>Specific usage description (book title, chapter, format)</li><li>Rights scope (territory, duration, format)</li><li>Credit line proposal</li><li>Response deadline</li><li>Contact information</li></ul><h3 id="follow-up-schedule" tabindex="-1">Follow-up Schedule <a class="header-anchor" href="#follow-up-schedule" aria-label="Permalink to &quot;Follow-up Schedule&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Day</th><th>Action</th></tr></thead><tbody><tr><td>Day 0</td><td>Initial request sent</td></tr><tr><td>Day 7</td><td>First follow-up if no response</td></tr><tr><td>Day 14</td><td>Second follow-up</td></tr><tr><td>Day 21</td><td>Final follow-up with deadline</td></tr><tr><td>Day 28</td><td>Escalate or close as unresponsive</td></tr></tbody></table><hr><h2 id="rule-of-10-hs-workflow" tabindex="-1">Rule of 10 - HS Workflow <a class="header-anchor" href="#rule-of-10-hs-workflow" aria-label="Permalink to &quot;Rule of 10 - HS Workflow&quot;">​</a></h2><h3 id="trigger-identification" tabindex="-1">Trigger Identification <a class="header-anchor" href="#trigger-identification" aria-label="Permalink to &quot;Trigger Identification&quot;">​</a></h3><p>Rule of 10 applies when:</p><ul><li>10+ items requested from same publication/publisher</li><li>Publisher is known rights holder</li><li>Bulk discount opportunity exists</li></ul><h3 id="publisher-contact-process" tabindex="-1">Publisher Contact Process <a class="header-anchor" href="#publisher-contact-process" aria-label="Permalink to &quot;Publisher Contact Process&quot;">​</a></h3><ol><li><strong>Inventory</strong> - Compile all items from source</li><li><strong>Publisher Research</strong> - Identify rights/permissions contact</li><li><strong>Request Preparation</strong> - Detailed list with specifications</li><li><strong>Submission</strong> - Send via preferred channel (email/portal)</li><li><strong>Tracking</strong> - Monitor in RPC as single grouped request</li></ol><h3 id="negotiation-points" tabindex="-1">Negotiation Points <a class="header-anchor" href="#negotiation-points" aria-label="Permalink to &quot;Negotiation Points&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Factor</th><th>Negotiation Approach</th></tr></thead><tbody><tr><td><strong>Fee Per Item</strong></td><td>Request bulk discount</td></tr><tr><td><strong>Usage Scope</strong></td><td>Clarify exact needs, avoid over-licensing</td></tr><tr><td><strong>Duration</strong></td><td>Match to publication lifecycle</td></tr><tr><td><strong>Format Rights</strong></td><td>Bundle print + electronic when possible</td></tr></tbody></table><hr><h2 id="hs-specific-considerations" tabindex="-1">HS-Specific Considerations <a class="header-anchor" href="#hs-specific-considerations" aria-label="Permalink to &quot;HS-Specific Considerations&quot;">​</a></h2><h3 id="medical-image-requirements" tabindex="-1">Medical Image Requirements <a class="header-anchor" href="#medical-image-requirements" aria-label="Permalink to &quot;Medical Image Requirements&quot;">​</a></h3><ul><li><strong>Patient Consent</strong> - Verify consent for identifiable images</li><li><strong>Model Releases</strong> - Required for recognizable individuals</li><li><strong>Clinical Accuracy</strong> - Note any required updates/disclaimers</li><li><strong>Institutional Approval</strong> - Some hospitals require IRB sign-off</li></ul><h3 id="common-hs-publishers" tabindex="-1">Common HS Publishers <a class="header-anchor" href="#common-hs-publishers" aria-label="Permalink to &quot;Common HS Publishers&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Publisher</th><th>Contact Method</th><th>Notes</th></tr></thead><tbody><tr><td><strong>Wolters Kluwer</strong></td><td>Online portal</td><td>Formal process</td></tr><tr><td><strong>Springer Nature</strong></td><td>RightsLink/Email</td><td>Generally responsive</td></tr><tr><td><strong>Wiley</strong></td><td>RightsLink</td><td>Standard fees</td></tr><tr><td><strong>BMJ</strong></td><td>Email</td><td>Academic pricing available</td></tr><tr><td><strong>NEJM</strong></td><td>Permissions email</td><td>Strict terms</td></tr></tbody></table><h3 id="hs-quality-checkpoints" tabindex="-1">HS Quality Checkpoints <a class="header-anchor" href="#hs-quality-checkpoints" aria-label="Permalink to &quot;HS Quality Checkpoints&quot;">​</a></h3><ol><li><strong>Source Accuracy</strong> - Is citation correct?</li><li><strong>Rights Holder Verification</strong> - Confirmed owner?</li><li><strong>Contact Information</strong> - Current and valid?</li><li><strong>Usage Scope</strong> - Matches editorial needs?</li><li><strong>Documentation</strong> - Complete RPC record?</li></ol>',48)])])}const m=e(s,[["render",n]]);export{p as __pageData,m as default};
