import{_ as t,c as r,o as i,ag as a}from"./chunks/framework.CQuhCYrb.js";const o="/diagrams/05_ST_Tier1_Chapter_Tracker.svg",m=JSON.parse('{"title":"Science & Technology (S&T) Workflows","description":"","frontmatter":{},"headers":[],"relativePath":"workflows/science-technology.md","filePath":"workflows/science-technology.md"}'),s={name:"workflows/science-technology.md"};function n(l,e,c,d,h,p){return i(),r("div",null,[...e[0]||(e[0]=[a('<h1 id="science-technology-s-t-workflows" tabindex="-1">Science &amp; Technology (S&amp;T) Workflows <a class="header-anchor" href="#science-technology-s-t-workflows" aria-label="Permalink to &quot;Science &amp; Technology (S&amp;T) Workflows&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Science &amp; Technology (S&amp;T) permissions handle scientific journals, technical publications, and research materials. S&amp;T uses Tiers 1-3 for RREF, Hot Topics, and MRW divisions.</p><h3 id="volume" tabindex="-1">Volume <a class="header-anchor" href="#volume" aria-label="Permalink to &quot;Volume&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Metric</th><th>Value</th></tr></thead><tbody><tr><td><strong>S&amp;T Projects/Year</strong></td><td>1,480</td></tr></tbody></table><h2 id="s-t-specific-characteristics" tabindex="-1">S&amp;T-Specific Characteristics <a class="header-anchor" href="#s-t-specific-characteristics" aria-label="Permalink to &quot;S&amp;T-Specific Characteristics&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Aspect</th><th>S&amp;T Characteristics</th></tr></thead><tbody><tr><td><strong>Content Types</strong></td><td>Research figures, data visualizations, technical diagrams</td></tr><tr><td><strong>Rights Holders</strong></td><td>Scientific publishers, research institutions, societies</td></tr><tr><td><strong>Divisions</strong></td><td>RREF, Hot Topics, MRW (Major Reference Works)</td></tr></tbody></table><h2 id="s-t-tier-definitions" tabindex="-1">S&amp;T Tier Definitions <a class="header-anchor" href="#s-t-tier-definitions" aria-label="Permalink to &quot;S&amp;T Tier Definitions&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Tier</th><th>Description</th></tr></thead><tbody><tr><td><strong>Tier 1</strong></td><td>Author obtains all permissions and provides log. CS validates and uploads logs/files to RPC</td></tr><tr><td><strong>Tier 2</strong></td><td>Author provides permission log. CS obtains permissions. CS uploads logs/files to RPC</td></tr><tr><td><strong>Tier 3</strong></td><td>Author submits manuscript. CS creates log and obtains permissions. CS uploads logs/files to RPC</td></tr></tbody></table><h3 id="role-key" tabindex="-1">Role Key <a class="header-anchor" href="#role-key" aria-label="Permalink to &quot;Role Key&quot;">​</a></h3><ul><li><strong>CS</strong> = Copyrights Specialist</li></ul><h2 id="tier-1-s-t-workflow" tabindex="-1">Tier 1 - S&amp;T Workflow <a class="header-anchor" href="#tier-1-s-t-workflow" aria-label="Permalink to &quot;Tier 1 - S&amp;T Workflow&quot;">​</a></h2><h3 id="chapter-tracker-integration" tabindex="-1">Chapter Tracker Integration <a class="header-anchor" href="#chapter-tracker-integration" aria-label="Permalink to &quot;Chapter Tracker Integration&quot;">​</a></h3><p>S&amp;T uses a Chapter Tracker system for workflow management:</p><p><img src="'+o+'" alt="S&amp;T Tier 1 Chapter Tracker"></p><h3 id="tier-1-process" tabindex="-1">Tier 1 Process <a class="header-anchor" href="#tier-1-process" aria-label="Permalink to &quot;Tier 1 Process&quot;">​</a></h3><ol><li>Author obtains all permissions</li><li>Author provides completed permission log</li><li>CS validates log completeness and accuracy</li><li>CS uploads logs and files to RPC</li></ol><hr><h2 id="tier-2-s-t-workflow" tabindex="-1">Tier 2 - S&amp;T Workflow <a class="header-anchor" href="#tier-2-s-t-workflow" aria-label="Permalink to &quot;Tier 2 - S&amp;T Workflow&quot;">​</a></h2><h3 id="tier-2-process" tabindex="-1">Tier 2 Process <a class="header-anchor" href="#tier-2-process" aria-label="Permalink to &quot;Tier 2 Process&quot;">​</a></h3><ol><li>Author provides permission log identifying items needing permission</li><li>CS reviews and validates the log</li><li>CS contacts rightsholders to obtain permissions</li><li>CS tracks responses and sends follow-up chasers</li><li>CS uploads completed logs and files to RPC</li></ol><hr><h2 id="tier-3-s-t-workflow-full-service" tabindex="-1">Tier 3 - S&amp;T Workflow (Full Service) <a class="header-anchor" href="#tier-3-s-t-workflow-full-service" aria-label="Permalink to &quot;Tier 3 - S&amp;T Workflow (Full Service)&quot;">​</a></h2><h3 id="tier-3-process" tabindex="-1">Tier 3 Process <a class="header-anchor" href="#tier-3-process" aria-label="Permalink to &quot;Tier 3 Process&quot;">​</a></h3><ol><li>Author submits manuscript only</li><li>CS reviews manuscript for third-party content</li><li>CS creates permission log from scratch</li><li>CS identifies all items requiring permissions</li><li>CS contacts all rightsholders</li><li>CS tracks all responses</li><li>CS uploads completed logs and files to RPC</li></ol><hr><h2 id="s-t-specific-considerations" tabindex="-1">S&amp;T-Specific Considerations <a class="header-anchor" href="#s-t-specific-considerations" aria-label="Permalink to &quot;S&amp;T-Specific Considerations&quot;">​</a></h2><h3 id="research-data-permissions" tabindex="-1">Research Data Permissions <a class="header-anchor" href="#research-data-permissions" aria-label="Permalink to &quot;Research Data Permissions&quot;">​</a></h3><ul><li><strong>Data Visualization</strong> - May need separate permission from data</li><li><strong>Supplementary Materials</strong> - Often different license than article</li><li><strong>Datasets</strong> - May have separate DOI and terms</li><li><strong>Code/Software</strong> - Check software license compatibility</li></ul><h3 id="conference-proceedings" tabindex="-1">Conference Proceedings <a class="header-anchor" href="#conference-proceedings" aria-label="Permalink to &quot;Conference Proceedings&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Scenario</th><th>Approach</th></tr></thead><tbody><tr><td><strong>IEEE Conference</strong></td><td>Through IEEE rights portal</td></tr><tr><td><strong>ACM Conference</strong></td><td>ACM Digital Library permissions</td></tr><tr><td><strong>Society Conference</strong></td><td>Contact society directly</td></tr><tr><td><strong>Independent Conference</strong></td><td>May revert to authors</td></tr></tbody></table><h3 id="preprint-considerations" tabindex="-1">Preprint Considerations <a class="header-anchor" href="#preprint-considerations" aria-label="Permalink to &quot;Preprint Considerations&quot;">​</a></h3><ul><li><strong>arXiv</strong> - Usually author retains rights</li><li><strong>bioRxiv</strong> - CC-BY common, verify version</li><li><strong>SSRN</strong> - Author agreement varies</li><li><strong>Version Matching</strong> - Ensure figures match published version</li></ul><h3 id="s-t-quality-checkpoints" tabindex="-1">S&amp;T Quality Checkpoints <a class="header-anchor" href="#s-t-quality-checkpoints" aria-label="Permalink to &quot;S&amp;T Quality Checkpoints&quot;">​</a></h3><ol><li><strong>DOI Verification</strong> - Correct article identified?</li><li><strong>Figure Numbering</strong> - Right figure from right paper?</li><li><strong>Version Control</strong> - Preprint vs. published version?</li><li><strong>Rights Transfer</strong> - Did author sign over rights?</li><li><strong>Supplementary Check</strong> - Is content from supplement?</li></ol>',35)])])}const u=t(s,[["render",n]]);export{m as __pageData,u as default};
